// src/pages/AskUs.jsx

import React, { useState, useEffect } from "react";
import axios from "axios";

const AskUs = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [queries, setQueries] = useState([]); // To store fetched queries

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "http://localhost:5000/api/askus/submit",
        formData
      );
      if (res.data.success) {
        alert("Your query has been submitted successfully!");
        setFormData({ name: "", email: "", message: "" });
        fetchQueries(); // Refresh queries after submitting
      }
    } catch (error) {
      console.error(error);
      alert("Something went wrong. Please try again.");
    }
  };

  const fetchQueries = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/askus/all");
      if (res.data.success) {
        setQueries(res.data.queries);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchQueries();
  }, []);

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 border rounded-lg shadow-md bg-white">
      <h2 className="text-2xl font-bold mb-6 text-center text-blue-600">
        Ask Us
      </h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={formData.email}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />
        <textarea
          name="message"
          placeholder="Your Message"
          value={formData.message}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded h-32"
        ></textarea>
        <button
          type="submit"
          className="w-full bg-blue-500 hover:bg-blue-600 text-white p-2 rounded font-semibold"
        >
          Submit
        </button>
      </form>

      {/* Display Submitted Queries */}
      <div className="mt-10">
        <h3 className="text-xl font-semibold mb-4 text-gray-700">
          Previous Queries:
        </h3>
        <ul className="space-y-4">
          {queries.length > 0 ? (
            queries.map((query) => (
              <li
                key={query._id}
                className="p-4 border rounded bg-gray-100 shadow-sm"
              >
                <p>
                  <strong>Name:</strong> {query.name}
                </p>
                <p>
                  <strong>Email:</strong> {query.email}
                </p>
                <p>
                  <strong>Message:</strong> {query.message}
                </p>
                <p className="text-sm text-gray-500">
                  Submitted on {new Date(query.createdAt).toLocaleString()}
                </p>
              </li>
            ))
          ) : (
            <p className="text-gray-500">No queries found.</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default AskUs;
