// src/pages/Login.jsx
import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // For React Router v5
// For React Router v6, use: import { useNavigate } from 'react-router-dom';

const Login = () => {
  const history = useHistory(); // Use history to navigate after login
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Example: send login credentials to the backend
      const res = await axios.post("http://localhost:5000/api/login", {
        email,
        password,
      });
      if (res.data.success) {
        // Store authToken and navigate to the next page
        localStorage.setItem("authToken", res.data.token);
        history.push("/dashboard"); // Redirect to dashboard after successful login
      }
    } catch (error) {
      console.error("Login failed", error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
